import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { fetchWebsites } from "../../actions";

class WebsiteList extends React.Component {
  componentDidMount() {
    this.props.fetchWebsites();
  }
  renderAdminButtons(stream) {
    if (stream.userId === this.props.currentUserId) {
      return (
        <div className="right floated content">
          <Link to={`/streams/edit/${stream.id}`} className="ui button primary">
            Edit
          </Link>
          <Link
            to={`/streams/delete/${stream.id}`}
            className="ui button negative"
          >
            Delete
          </Link>
        </div>
      );
    }
  }
  renderList() {
    return this.props.streams.map((stream) => {
      return (
        <div className="item" key={stream.id}>
          {this.renderAdminButtons(stream)}
          <i className="large middle aligned icon camera" />
          <div className="content">
            <Link to={`streams/${stream.id}`} className="header">
              {stream.title}
            </Link>
            <div className="description">{stream.description}</div>
          </div>
        </div>
      );
    });
  }
  renderCreate() {
    if (this.props.isSignedIn) {
      return (
        <div style={{ textAlign: "right" }}>
          <Link to="/websites/new" className="ui button primary">
            Create Website
          </Link>
        </div>
      );
    }
  }
  render() {
    return (
      <div>
        <div>
          <h2>Websites</h2>
          <div className="ui celled list">{this.renderList()}</div>
          {this.renderCreate()}
        </div>
        WebsiteList
        <Link
          to={{ pathname: "/passwords/new", aboutProps: { website: "One" } }}
        >
          Click me
        </Link>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    websites: Object.values(state.streams),
    currentUserId: state.auth.userId,
    isSignedIn: state.auth.isSignedIn,
  };
};

export default connect(mapStateToProps, { fetchWebsites })(WebsiteList);
